import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import api from '../../api/api'; // Assumindo que voc� tem uma inst�ncia do Axios configurada

const Users = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await api.get('/api/users', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`, // Envia o token no cabe�alho
                    },
                });
                setUsers(response.data);
                setLoading(false);
            } catch (error) {
                console.error('Erro ao buscar usu�rios:', error);
                setLoading(false);
                setErrorMessage('Erro ao carregar usu�rios. Tente novamente mais tarde.');
            }
        };

        fetchUsers();
    }, []);

    if (loading) {
        return <p>Carregando...</p>;
    }

    return (
        <div>
            <h2>Lista de Usu�rios</h2>
            <ul>
                {users.map((user) => (
                    <li key={user.id}>
                        <p>{user.name} - {user.email}</p>
                        <Link to={`/update-user/${user.id}`}>Atualizar</Link> | 
                        <Link to={`/delete-user/${user.id}`}>Deletar</Link>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Users;
