import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext'; 
import api from '../../api/api'; 

const UsersManagement = () => {
    const [users, setUsers] = useState([]); 
    const [loading, setLoading] = useState(true); 
    const [errorMessage, setErrorMessage] = useState(null); 
    const { user } = useAuth(); 
    const navigate = useNavigate(); 

    // Fun��o para buscar os usu�rios na API
    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await api.get('/api/users'); // Endpoint para listar usu�rios
                setUsers(response.data);
                setLoading(false);
            } catch (error) {
                console.error('Erro ao buscar usu�rios:', error);
                setErrorMessage('Falha ao carregar os usu�rios.');
                setLoading(false);
            }
        };
        fetchUsers();
    }, []);

    // Fun��o para deletar um usu�rio
    const handleDelete = async (userId) => {
        if (userId === user.id) {
            alert('Voc� n�o pode deletar o pr�prio usu�rio.');
            return;
        }
        try {
            await api.delete(`/api/users/${userId}`); // Endpoint para deletar usu�rio
            setUsers(users.filter((u) => u.id !== userId)); // Remove o usu�rio da lista localmente
            alert('Usu�rio deletado com sucesso.');
        } catch (error) {
            console.error('Erro ao deletar usu�rio:', error);
            setErrorMessage('Falha ao deletar o usu�rio.');
        }
    };

    // Fun��o para redirecionar para a p�gina de edi��o
    const handleEdit = (userId) => {
        navigate(`/edit-user/${userId}`); // Endpoint para a p�gina de edi��o
    };

    if (loading) return <p>Carregando usu�rios...</p>;
    if (errorMessage) return <p>{errorMessage}</p>;

    return (
        <div>
            <h1>Gerenciamento de Usu�rios</h1>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nome</th>
                        <th>Email</th>
                        <th>A��es</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((u) => (
                        <tr key={u.id}>
                            <td>{u.id}</td>
                            <td>{u.name}</td>
                            <td>{u.email}</td>
                            <td>
                                <button onClick={() => handleEdit(u.id)}>Editar</button>
                                <button onClick={() => handleDelete(u.id)}>Excluir</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default UsersManagement;
