import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext'; // Para acessar o usu�rio logado e o token
import api from '../../api/api';

const Users = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [errorMessage, setErrorMessage] = useState(null); // Aqui est� o estado para mensagens de erro
    const { user } = useAuth(); // Obt�m o usu�rio logado
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await api.get('/api/users', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`, // Envia o token no cabe�alho
                    },
                });
                setUsers(response.data);
                setLoading(false);
            } catch (error) {
                console.error('Erro ao buscar usu�rios:', error);
                setLoading(false);
                setErrorMessage('Erro ao carregar usu�rios. Tente novamente mais tarde.');
            }
        };

        fetchUsers();
    }, []);

    const handleDelete = (id) => {
        if (user.id === id) {
            alert('Voc� n�o pode excluir seu pr�prio usu�rio!');
            return;
        }

        navigate(`/delete-user/${id}`); // Redireciona para a p�gina de dele��o
    };

    if (loading) {
        return <p>Carregando usu�rios...</p>;
    }

    return (
        <div>
            <h2>Lista de Usu�rios</h2>
            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>} {/* Mensagem de erro */}
            <ul>
                {users.map((user) => (
                    <li key={user.id}>
                        {user.name} - {user.email}
                        <button onClick={() => handleDelete(user.id)}>Excluir</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Users;
