import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../api/api';
import { Container, Form, Input, Button, SwitchMessage, ErrorMessage } from '../';
import { useAuth } from '../../hooks/useAuth';

const Login = () => {
    const [formData, setFormData] = useState({ email: '', password: '' });
    const [error, setError] = useState(null); // Estado para armazenar mensagens de erro
    const { login } = useAuth();
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await api.post('/api/auth/login', formData);
            console.log('Login realizado com sucesso:', response.data);
            login(response.data.token); // Salva o token no contexto
            navigate('/home'); // Redireciona para a Home
        } catch (err) {
            setError(err.response?.data.message || 'Erro desconhecido'); // Define a mensagem de erro
            console.error('Erro ao fazer login:', err.response?.data.message);
        }
    };

    return (
        <Container>
            <Form onSubmit={handleSubmit}>
                <h2>Login</h2>
                {error && <ErrorMessage>{error}</ErrorMessage>} {/* Mostra o erro, se houver */}
                <Input 
                    name="email" 
                    placeholder="Email" 
                    type="email" 
                    onChange={handleChange} 
                    required 
                />
                <Input 
                    name="password" 
                    placeholder="Senha" 
                    type="password" 
                    onChange={handleChange} 
                    required 
                />
                <Button type="submit">Entrar</Button>
                <SwitchMessage>
                    Nï¿½o tem uma conta? <a href="/register">Registre-se</a>
                </SwitchMessage>
            </Form>
        </Container>
    );
};

export default Login;
