import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../../api/api';

const DeleteUser = () => {
    const { id } = useParams();
    const navigate = useNavigate();

    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchUser = async () => {
            try {
                const response = await api.get(`/api/users/${id}`);  // Substitua pela URL correta
                setUser(response.data);
                setLoading(false);
            } catch (error) {
                console.error('Erro ao buscar usu�rio:', error);
                setLoading(false);
            }
        };

        fetchUser();
    }, [id]);

    const handleDelete = async () => {
        try {
            await api.delete(`/api/users/${id}`);  // Substitua pela URL correta
            navigate('/users');
        } catch (error) {
            console.error('Erro ao deletar usu�rio:', error);
        }
    };

    if (loading) {
        return <p>Carregando...</p>;
    }

    return (
        <div>
            <h2>Deletar Usu�rio</h2>
            <p>Tem certeza que deseja excluir o usu�rio {user.name}?</p>
            <button onClick={handleDelete}>Sim, excluir</button>
            <button onClick={() => navigate('/users')}>Cancelar</button>
        </div>
    );
};

export default DeleteUser;
